mapper.select:
SELECT


,xiangmu.yonghu_id as xiangmuYonghuId
,xiangmu.xiangmu_uuid_number as xiangmuUuidNumber
,xiangmu.xiangmu_name as xiangmuName
,xiangmu.xiangmu_address as xiangmuAddress
,xiangmu.xiangmu_file as xiangmuFile
,xiangmu.xiangmu_types as xiangmuTypes
,xiangmu.xiangmu_kucun_number as xiangmuKucunNumber
,xiangmu.shenqing_time as shenqingTime
,xiangmu.nachuan_time as nachuanTime
,xiangmu.xiangmu_content as xiangmuContent
,yuangong.yuangong_uuid_number as yuangongUuidNumber
,yuangong.yuangong_name as yuangongName
,yuangong.yuangong_phone as yuangongPhone
,yuangong.yuangong_id_number as yuangongIdNumber
,yuangong.yuangong_photo as yuangongPhoto
,yuangong.bumen_types as bumenTypes
,yuangong.zhiwei_types as zhiweiTypes
,yuangong.yuangong_email as yuangongEmail
,yuangong.jinyong_types as jinyongTypes

FROM jianzaojindu  a
left JOIN xiangmu xiangmu ON a.xiangmu_id = xiangmu.id
left JOIN yuangong yuangong ON a.yuangong_id = yuangong.id



        and a.id in

        #{item}



        and (
            a.xiangmu_id = #{params.xiangmuId}
        )


        and (
            a.yuangong_id = #{params.yuangongId}
        )


        and a.jianzaojindu_uuid_number like CONCAT('%',#{params.jianzaojinduUuidNumber},'%')


        and a.jianzaojindu_name like CONCAT('%',#{params.jianzaojinduName},'%')


        and a.jianzaojindu_types = #{params.jianzaojinduTypes}


          and a.jianzaojindu_zhengchang_number >= #{params.jianzaojinduZhengchangNumberStart}   


          and a.jianzaojindu_zhengchang_number <= #{params.jianzaojinduZhengchangNumberEnd}   


        and a.jianzaojindu_zhengchang_number = #{params.jianzaojinduZhengchangNumber}


          and a.jianzaojindu_xianzai_number >= #{params.jianzaojinduXianzaiNumberStart}   


          and a.jianzaojindu_xianzai_number <= #{params.jianzaojinduXianzaiNumberEnd}   


        and a.jianzaojindu_xianzai_number = #{params.jianzaojinduXianzaiNumber}


          and UNIX_TIMESTAMP(a.jianzaojindu_time) >= UNIX_TIMESTAMP(#{params.jianzaojinduTimeStart}) 


          and UNIX_TIMESTAMP(a.jianzaojindu_time) <= UNIX_TIMESTAMP(#{params.jianzaojinduTimeEnd}) 


        and a.jianzaojindu_time = #{params.jianzaojinduTime}


        and a.jianzaojindu_content like CONCAT('%',#{params.jianzaojinduContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.xiangmu_id IS NOT NULL


        and xiangmu.yonghu_id = #{params.yonghuId}


        and xiangmu.xiangmu_uuid_number like CONCAT('%',#{params.xiangmuUuidNumber},'%')


        and xiangmu.xiangmu_name like CONCAT('%',#{params.xiangmuName},'%')


        and xiangmu.xiangmu_address like CONCAT('%',#{params.xiangmuAddress},'%')


        and xiangmu.xiangmu_types = #{params.xiangmuTypes}



          and xiangmu.xiangmu_kucun_number >= #{params.xiangmuKucunNumberStart}   


          and xiangmu.xiangmu_kucun_number <= #{params.xiangmuKucunNumberEnd}   


        and xiangmu.xiangmu_kucun_number = #{params.xiangmuKucunNumber}


          and UNIX_TIMESTAMP(xiangmu.shenqing_time) >= UNIX_TIMESTAMP(#{params.shenqingTimeStart}) 


          and UNIX_TIMESTAMP(xiangmu.shenqing_time) <= UNIX_TIMESTAMP(#{params.shenqingTimeEnd}) 


        and a.shenqing_time = #{params.shenqingTime}


          and UNIX_TIMESTAMP(xiangmu.nachuan_time) >= UNIX_TIMESTAMP(#{params.nachuanTimeStart}) 


          and UNIX_TIMESTAMP(xiangmu.nachuan_time) <= UNIX_TIMESTAMP(#{params.nachuanTimeEnd}) 


        and a.nachuan_time = #{params.nachuanTime}


        and xiangmu.xiangmu_content like CONCAT('%',#{params.xiangmuContent},'%')


          and UNIX_TIMESTAMP(xiangmu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(xiangmu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



        and a.yuangong_id IS NOT NULL


        and yuangong.yuangong_uuid_number like CONCAT('%',#{params.yuangongUuidNumber},'%')


        and yuangong.yuangong_name like CONCAT('%',#{params.yuangongName},'%')


        and yuangong.yuangong_phone like CONCAT('%',#{params.yuangongPhone},'%')


        and yuangong.yuangong_id_number like CONCAT('%',#{params.yuangongIdNumber},'%')


        and yuangong.bumen_types = #{params.bumenTypes}



        and yuangong.zhiwei_types = #{params.zhiweiTypes}



        and yuangong.yuangong_email like CONCAT('%',#{params.yuangongEmail},'%')


        and yuangong.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
